package by.gsu.client;

import java.io.IOException;
import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.atomic.AtomicLong;

import org.omg.CORBA.NO_IMPLEMENT;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import com.fasterxml.jackson.core.JsonParseException;
import com.fasterxml.jackson.databind.JsonMappingException;

import by.gsu.SignPositionService.models.Sign;
import by.gsu.SignPositionService.models.Point;

public class ClientInMemory  implements ISignPositionClient{
	private static final Logger LOG = LoggerFactory.getLogger(ClientInMemory.class);
	private static AtomicLong id = new AtomicLong(10);

	public static void main(String[] args) {
		ClientInMemory client = new ClientInMemory();
		System.out.println("run");
		try {

			client.getListSigns();
			client.methodGetSign(3);

			Sign sign = new Sign(1, "eeee", "eeee", new Point(30.0, 20.1));
			client.methodPostSign(sign);
			client.methodPostSign(sign);
			client.methodPostSign(sign);
			client.methodPostSign(sign);
			client.methodPostSign(sign);

			client.getListSigns();

			client.methodGetSign(3);
			client.methodPutSign(sign);
			client.methodPutSign(sign);
			client.methodPutSign(sign);

			client.methodGetSign(3);

			client.getListSigns();

		} catch (IOException e) {
			e.printStackTrace();
		}

	}

	private List<Sign> store = new ArrayList<Sign>() {
		{
			add(new Sign(1));
			add(new Sign(2, "qwer.jpg", "cats fail", new Point(30, 50)));
			add(new Sign(3, "aaa.jpg", "tortle fail", new Point(30, 50),
					"<img src='data:image/jpeg;base64,'/>"));
			add(new Sign(4, "ssss.jpg", "dogs fail", new Point(30, 50)));
			add(new Sign(5, "rad.jpg", "rad fail", new Point(42, 34),
					""));
		}
	};

	public ClientInMemory() {
		super();
	}

	public String getHost() {
		return "IN_MEMORY" + System.clearProperty("name");
	}

	public List<Sign> getListSigns() throws JsonParseException, JsonMappingException, IOException {
		System.out.println("GET LIST: OK; " + store);
		for (Sign sign : store) {
			System.out.println("\t\t" + sign);
		}
		return store;
	}

	public void methodDeleteSign(long id) {
		for (Sign sign : store) {
			if (sign.getId() == id) {
				store.remove(sign);
				System.out.println("DELETE: OK");
				return;
			}
		}
		System.out.println("DELETE: NOT FOUND");
	}

	public Sign methodGetSign(long id) throws JsonParseException, JsonMappingException, IOException {
		for (Sign sign : store) {
			if (sign.getId() == id) {
				System.out.println("GET: OK; " + sign);
				return sign;
			}
		}
		System.out.println("GET: NOT FOUND; ");
		return null;
	}

	public void methodPostSign(Sign sign) {
		long idTmp = id.incrementAndGet();
		sign.setId(idTmp);
		store.add(sign);
		System.out.println("POST: OK; " + sign);
	}

	public void methodPutSign(Sign sign) {
		for (int i = 0; i < store.size(); i++) {
			Sign p = store.get(i);
			if (p.getId() == sign.getId()) {
				System.out.println("PUT: UPDATE; \n\t old:" + p + "\n\tnew:" + sign);
				store.set(i, sign);
				return;
			}
		}

		long idTmp = id.incrementAndGet();
		sign.setId(idTmp);
		store.add(sign);
		System.out.println("PUT: CREATE; new id = " + idTmp + ";" + sign);
	}

	public void setHost(String host) {
		throw new NO_IMPLEMENT();
	}

}
